import React, { useEffect, useState } from "react";
import { fetchReviews } from "./api";

function App() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    fetchReviews().then(data => {
      setReviews(data);
      setLoading(false);
    }).catch(e => {
      setErr(e.message); setLoading(false);
    });
  }, []);

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h1>WhatsApp Product Reviews</h1>
      {loading && <p>Loading...</p>}
      {err && <p style={{ color: "red" }}>{err}</p>}
      {!loading && !err && (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th style={{borderBottom: "1px solid #ccc", textAlign: "left"}}>Name</th>
              <th style={{borderBottom: "1px solid #ccc", textAlign: "left"}}>Product</th>
              <th style={{borderBottom: "1px solid #ccc", textAlign: "left"}}>Review</th>
              <th style={{borderBottom: "1px solid #ccc", textAlign: "left"}}>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {reviews.map(r => (
              <tr key={r.id}>
                <td style={{ padding: "8px 4px" }}>{r.user_name}</td>
                <td style={{ padding: "8px 4px" }}>{r.product_name}</td>
                <td style={{ padding: "8px 4px" }}>{r.product_review}</td>
                <td style={{ padding: "8px 4px" }}>{new Date(r.created_at).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
